    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        
        double inicial, interes, fin;
        
        inicial = Double.parseDouble(txtInv.getText());
        interes = Double.parseDouble(txtInMen.getText());
        fin = Double.parseDouble(txtInvFinal.getText());
        taMeses.setText("");
        int mes = 1;
        double saldo = inicial;
        while (saldo < fin){
            saldo = saldo * (1 + interes/100);
            taMeses.append(" mes " + mes + "Saldo = " + saldo + "\n");
            mes++;
        }
    }//GEN-LAST:event_jButton1ActionPerformed

